#include <iostream>using namespace std;class Animal {public:    Animal(char const* n): name(n) {}    virtual void make_noise() = 0;protected:    string name;};class HasLegs {public:    HasLegs(int n) : numLegs(n) {}    int get_legs() const { return numLegs; }protected:    int numLegs;};class Dog: public Animal, public HasLegs {public:    Dog(char const* name): Animal(name), HasLegs(4) {}    void make_noise();};void Dog::make_noise() {    cout << name << " the dog says \"woof\"" << endl;}int main(void) {    Dog d = Dog("Earl");    d.make_noise();    cout << "He has " << d.get_legs() << " legs" << endl;    Dog f = Dog("Fiona");    Animal *a2 = &f;    a2->make_noise();    cout << endl;}